package com.example.springBootTest.domain;import java.io.Serializable;import org.springframework.stereotype.Repository;@Repositorypublic class RoleAuthority implements Serializable {	/**	 * 	 */	private static final long serialVersionUID = 1L;		/* 角色ID */	private Integer roleId;	/* OP用户ID */	private Integer authorityId;		public Integer getRoleId() {		return roleId;	}	public void setRoleId(Integer roleId) {		this.roleId = roleId;	}	public Integer getAuthorityId() {		return authorityId;	}	public void setAuthorityId(Integer authorityId) {		this.authorityId = authorityId;	}	}